import React, {Component} from 'react';
import { connect } from 'react-redux';

class InputForm extends Component {

    handleChange = (event) => {
      this.props.changeText(event.target.value);
    }

    handleSubmit = (event) => {
      let newTodo = {todo: event.target.value, description: "", id: this.props.size + 1};
      this.props.addTodo()
    }

    render() {
        return (
          <div id="tasklist" className="tasklist">
              <form id="InputForm" onSubmit={this.handleSubmit}>
                <input
                  type="text"
                  autoComplete="off"
                  id="textform"
                  onChange={this.handleChange}
                  placeholder="Leave a message for yourself"
                  value={this.props.text}
                />
                <button className="button">Save</button>
              <br />
              <br />
            </form>
          </div>)
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    changeText: (text) => { dispatch({type: 'UPDATE_TEXT', text: text})},
    addTodo: (todo) => { dispatch({type: 'ADD_TODO', todo: todo})}
  }
}

const mapStateToProps = (state) => {
  return {
    text: state.text,
    id: state.id
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(InputForm)
