      if (todoEntry.show == true)
        return (
          <div class="todoEntry" key={todoEntry.id} onClick={this.handleDeletion}>
              {todoEntry.todo}
          </div>
        );
    });

    return (
      <div className="todoList">
        { todoList }
      </div>
    );
}

export default Todo;
