const initState = {
  todos: [
    {todo: "Cook dinner", description:"Burgers and fries", id: 1, show:true},
    {todo: "Do laundry", description:"Yup", id: 2, show:true}
  ],
  index: true,
  size: 2,
  showPopUp: true
}

const rootReducer = (state = initState, action) => {
  return state;
}

export default rootReducer
