import React from "react";

const Todo = (props, deleteTodo) => {
    const { todos } = props;

    const todoList = todos.map(todoEntry => {
      if (todoEntry.show == true)
        return (
          <div class="todoEntry" key={todoEntry.id}>
              {todoEntry.todo}
          </div>
        );
    });

    return (
      <div className="todoList">
        <span onClick={() => {deleteTodo(todoEntry.id)}}>{ todoList }</span>
      </div>
    );
}

export default Todo;
