import React from "react";

const Todo = ({todos, deleteTodo}) => {

    const todoList =  todos.length ? (
      todos.map(todoEntry => {
        return (
          <div className="todoEntry" key={todoEntry.id}>
              <p><div onClick={() => { deleteTodo(todoEntry.id) } }>{todoEntry.todo}</div></p>
              {(todoEntry.show === true) ? {todoEntry, description} : null }
          </div>
        )
      })
    ) : (
      <div className="emptyTodo">
        <p>Your list is empty. Time to fill it up!</p>
      </div>
    );

    return (
      <div className="todoList">
         { todoList }
      </div>
    );
}

export default Todo;
