import React, {Component} from 'react';
import { connect } from 'react-redux';

class InputForm extends Component {

    handleChange = (event) => {
      this.props.changeText(event.target.value);
    }

    render() {
        return (
          <div id="tasklist" className="tasklist">
              <form id="InputForm" onSubmit={this.addTodo}>
                <input
                  type="text"
                  autoComplete="off"
                  id="textform"
                  onChange={() => {this.handleChange(this.value)}}
                  placeholder="Leave a message for yourself"
                  value={this.props.text}
                  onSubmit={this.clearform}
                />
                <button className="button">Save</button>
              <br />
              <br />
            </form>
          </div>)
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    changeText: (text) => { dispatch({type: 'UPDATE_TEXT', text: text})}
  }
}

const mapStateToProps = (state) => {
  return {
    text: state.text
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(InputForm)
