import React from "react";

const Todo = ({props}, {deleteTodo}) => {
    const { todos } = props;

    const todoList = todos.map(todoEntry => {
      if (todoEntry.show === true)
        return (
          <div className="todoEntry" key={todoEntry.id}>
              <span onClick={() => {deleteTodo(todoEntry.id)}}>{todoEntry.todo}</span>
          </div>
        );
    });

    return (
      <div className="todoList">
         { todoList }
      </div>
    );
}

export default Todo;
