import React from "react";

const Todo = ({todos, deleteTodo}) => {

    const todoList =  todos.length ? (
      todos.map(todoEntry => {
        return (
          <div className="todoEntry" key={todoEntry.id}>
              <div onClick={() => { deleteTodo(todoEntry.id) } }>{todoEntry.todo}</div>
          </div>
        )
      })
    ) : (
      <p className="emptyTodo">
        Your list is empty. Time to fill it up!
      </p>
    );

    return (
      <div className="todoList">
         { todoList }
      </div>
    );
}

export default Todo;
