import React, { Component } from 'react'
import './index.css'
import InputForm from './InputForm.js'
import Todo from './Todo.js';

class App extends Component {
  state = {
    todos: [
      {todo: "Finish this project", description:"", id: 1, show:true},
      {todo: "Complete BCS program", description:"", id: 2, show:true}
    ],
    index: true,
    size: 2
  }

  addTodo = todo => {
    this.setState({
      todos: this.state.todos.concat(todo),
      size: this.state.size + 1
    })
  }

  deleteTodo = (id) => {
    const todosFilt = this.state.todos.filter(todo => {
      if (todo.id === id) {
        todo.setState({
          show: false
        })
      }
    });

    this.setState({
      todos: todosFilt
    });
  }

  handleHome = e => {
    this.setState({
      index: true
    })
  }

  handleAbout = e => {
    this.setState({
      index: false
    })
  }

  render() {
    if (this.state.index === true) {
      return (
        <div className="App">
          <div className="body">
            <div className="siteheader">LIST</div>
            <div className="siteheader" onClick={this.handleAbout}>ABOUT</div>
            <InputForm addTodo={this.addTodo} size={this.state.size}/>
            <Todo todos={this.state.todos} deleteTodo={this.deleteTodo}/>
          </div>
        </div>
    );} else {
      return (
        <div className="App">
          <div className="body">
            <div className="siteheader" onClick={this.handleHome}>LIST</div>
            <div className="siteheader" onClick={this.handleAbout}>ABOUT</div>
          </div>

          <div className="about">Testx</div>
        </div>
      )
    }
  }
}

export default App;
