import React, {Component} from 'react';
import { connect } from 'react-redux';

class InputForm extends Component {

  handleChange = (text) => {
    this.props.newText(text);
  }

  addTodo = () => {

  }

  render() {
      return (
        <div id="tasklist" className="tasklist">
            <form id="InputForm" onSubmit={this.addTodo}>
              <input
                type="text"
                autoComplete="off"
                id="textform"
                onChange={() => {this.handleChange(this.text)}}
                placeholder="Leave a message for yourself"
                value={this.text}
                onSubmit={this.clearform}
              />
              <button className="button">Save</button>
            <br />
            <br />
          </form>
        </div>)
  }
}

const mapDispatchToProps = (dispatch) => {
  console.log("Hi");
  return {
    newText: (text) => { dispatch({type: 'UPDATE_TEXT', text: text})}
  }
}

const mapStateToProps = (state) => {
  return {
    text: state.text
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(InputForm)
