import React from "react";

class TodoList extends React.Component {
  state = {
    todo: ["Take dog for walk", "Finish school"],
    text: "",
    showPopup: false
  };

  handleChange = e => {
    this.setState({
      text: e.target.value
    });
  };

  handleSubmit = e => {
    e.preventDefault();
    this.setState({
      todo: this.state.todo.concat(this.state.text)
    });
  };

  render() {
    if (this.state.homePage) {
      return (
        <div id="tasklist" className="tasklist">
          <div id="sitebody" class="header">
            <form onSubmit={this.handleSubmit}>
              <input
                type="text"
                id="textform"
                onChange={this.handleChange}
                placeholder="Type something"
              />
              <button class="button">Add</button>
            </form>
            <br />
            <br />
          </div>
          Things to do:
          <ul id="todo">
            {this.state.todo.map(x => (
              <li>
                <div onClick={this.togglePopup}>{x}</div>
              </li>
            ))}
          </ul>
        </div>)
    }
  }
}

export default TodoList;
