import React from "react";

class TodoList extends React.Component {
  state = {
    homePage: true,
    todo: ["Take dog for walk", "Finish school"],
    text: "",
    showPopup: false
  };

  handleChange = e => {
    this.setState({
      text: e.target.value
    });
  };

  handleSubmit = e => {
    e.preventDefault();
    this.setState({
      todo: this.state.todo.concat(this.state.text)
    });
  };

  togglePopup() {
    this.setState({
      showPopup: !this.state.showPopup
    });
  }

  render() {
    if (this.state.homePage) {
      return (
        <div id="tasklist" className="tasklist">
          <div id="sitebody" class="header">
            <form onSubmit={this.handleSubmit}>
              <input
                type="text"
                id="textform"
                onChange={this.handleChange}
                placeholder="Type something"
              />
              <button class="button">Add</button>
            </form>
            <br />
            <br />
          </div>
          Things to do:
          <ul id="todo">
            {this.state.todo.map(x => (
              <li>
                <div onClick={this.togglePopup}>{x}</div>
              </li>
            ))}
          </ul>
          {this.state.showPopup ? (
            <Details text="Close Me" closePopup={this.togglePopup.bind(this)} />
          ) : null}
        </div>
      );
    } else {
      return (
        <div id="tasklist" className="tasklist">
          <div id="about" class="about">
            This is a message list website.
            <br />
            The purpose of this website is to allow
            <br />
            you to add and delete tasks from a to-do list.
          </div>
        </div>
      );
    }
  }
}

export default TodoList;
